package class3;

public class forloopexercises2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;
		for(int i = 1; i <= 10; i++) {
			sum = sum + i;
	}
		System.out.println(sum);
		
	//find and print the sum of all the numbers from 1 to 100
		int sum2 = 0;
		for(int i = 1; i <= 100; i++) {
			sum2 = sum2 + i;
	}
		System.out.println(sum2);
	
	// find and print the sum of all the odd numbers from 1 to 1000
		int sum3 = 0;
		for(int i = 1; i <= 1000; i = i + 2) {
			sum3 = sum3 + i;
		}
			
		System.out.println(sum3);
		
	//find and print the sum of all the even numbers from 200 to 10200
		int sum4 = 0;
		for(int i = 200; i <= 10200; i = i + 2) {
			sum4 = sum4 + i;
		}
			
		System.out.println(sum4);
}}





