package class4;

public class switchstatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
		switch(a) {
			case 3:
				System.out.println(a);
				break;
			case 4:
				System.out.println("a is 4");
				break;
			default:
				System.out.println("a is not 3 or 4");
				
				
				
		}

	}

}
